const auth = (state={}, action) => {
    switch (action.type) {
        case 'LOGOUT':
         return {
             logged: false,
             token: ''
         }

        case 'LOGIN': 
         return {
             logged: true,
             token: action.token
         }
        default:
         return {
             logged: false,
             token: ''
         }
    }
}

export default auth;
