import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Form, Input, Button, Upload, Icon, Select, Modal, message, } from 'antd'
import { Link,Route } from 'react-router-dom'
import reqwest from 'reqwest'

import { theUrl , tokenHeaders } from 'selfConfig'
import { PreRoute, UploadWeChat } from '../../public_component/index'

// page structor
const postCommodity = ({match, auth}) => {
    return(
        <div>
            <Link 
                to={{
                    pathname: "/user_profile/postedCommodity/newPost",
                    state: {
                        username: auth.username
                    }
                }}
            >Create New Post</Link>
            <Route 
                path={"/user_profile/postedCommodity/newPost"   }
                component={NewPost}
            />
            <LivingPostList />
        </div>
    )
}


class  newPost extends Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
            // location.state is passed by <Link>
            username: this.props.location.state.username,
            payload: []
        }

        this.handleSubmit = this.handleSubmit.bind(this)
    }

    async componentDidMount() {
        const url = theUrl + '/userProfile'

        const payload = await fetch(url, {
            headers: tokenHeaders(localStorage.getItem("token")),
            method: 'POST',
            body: JSON.stringify({
            username: this.state.username
            })
        }).then( res => res.json() )

        this.setState({
            loading: false,
            payload: payload[0]
        })
    }

    handleSubmit(e) {
        e.preventDefault();
        this.props.form.validateFields((err, values)=> {
            if(!err) {

              console.log(this.state.payload)
              console.log(values)

              const {payload} = this.state
              const url = theUrl + '/createCard'
              var date = new Date()
              date = date.getUTCFullYear() + '-' +
                    ('00' + (date.getUTCMonth()+1)).slice(-2) + '-' +
                    ('00' + date.getUTCDate()).slice(-2) + ' ' + 
                    ('00' + date.getUTCHours()).slice(-2) + ':' + 
                    ('00' + date.getUTCMinutes()).slice(-2) + ':' + 
                    ('00' + date.getUTCSeconds()).slice(-2);

              fetch(url, {
                headers: tokenHeaders(localStorage.getItem("token")),
                method: 'POST',
                body: JSON.stringify({
                  uid: payload.uid,
                  commodity: values.commoidty,
                  price: values.price,
                  des: values.des,
                  post_time: date,
                  category: values.category
                })
              })
            }
        })
    }


    render() {
        const {payload} = this.state
        const { getFieldDecorator } = this.props.form
        const { TextArea } = Input 
        const { Option } = Select 
        return(
            <Form onSubmit={this.handleSubmit}>
                {/* personal information */}
                <Form.Item label="phone number">
                {getFieldDecorator('phone', {
                    initialValue: payload.phone,
                })(
                    <Input 
                        username={payload.username}
                    />
                )}
                </Form.Item>
                <Form.Item>
                    <Test />
                </Form.Item>

                <Form.Item label="commodity">
                {getFieldDecorator('commodity', {
                    rules: [{required: true, message: '商品名不能为空'}]
                })(
                    <Input />
                )}
                </Form.Item>
                <Form.Item label="price">
                {getFieldDecorator('price', {
                    rules: [{required: true, message: '商品价格不能为空'}]
                })(
                    <Input />
                )}
                </Form.Item>

                <Form.Item label="description">
                {getFieldDecorator('des', {
                    rules: [{required: true, message: '商品描述不能为空'}]
                })(
                    <TextArea />
                )}
                </Form.Item>

                <Form.Item label="分类">
                {getFieldDecorator('category', {
                    initialValue: "1",
                    rules: [{required: true, message: '商品描述不能为空'}]
                })(
                  <Select>
                    <Option value="1">书籍</Option>
                    <Option value="2">电子产品</Option>
                    <Option value="3">外快</Option>
                    <Option value="0">others</Option>
                  </Select>
                )}
                </Form.Item>

                <Form.Item>
                    <Button 
                        type="primary"
                        htmlType="submit"
                    >Create Now!</Button>
                </Form.Item>
            </Form>
        )
    }
}

const LivingPostList = () => {
    return (
        <h1>Living Commodity List</h1>
    )
}

// =================================================================================================
// test part
class PallWrop extends React.Component {
  state = {
    previewVisible: false,
    previewImage: '',
  };

  handlePreview = file => {
    this.setState({
      previewImage: file.url || file.thumbUrl,
      previewVisible: true,
    });
  };
  render() {
    const { fileList, imgNumber,onChange } = this.props;
    const list = fileList||[]
    const { previewVisible, previewImage } = this.state;
    const uploadButton = (
      <div>
        <Icon type="plus" />
        <div className="ant-upload-text">Upload</div>
      </div>
    );
    return (
      <div className="clearfix">
        <Upload
          action="http://localhost:7001/api/upload"
          listType="picture-card"
          fileList={list}
          onChange={onChange}
          onPreview={this.handlePreview}
        >
          {list.length >= imgNumber ? null : uploadButton}
        </Upload>
        <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
          <img alt="example" style={{ width: '100%' }} src={previewImage} />
        </Modal>
      </div>
    );
  }
}

const FormItem = Form.Item;
class Test extends React.Component {
  state = {
    fileList: [
      {
        status: 'done',
        uid: 0,
        url: 'http://localhost:7001/public/images/kele.jpg',
      },
    ],
  };

  componentDidMount() {
    const { form } = this.props;
    const { fileList } = this.state;
    form.setFieldsValue({ pictures: fileList });
  }

  handleOnChange = ({ fileList }) => {
    console.log(fileList)
    return fileList.map(file => ({
      status: file.status,
      uid: file.uid,
      url: file.response?file.response.data.url:file.url,
    }));
  };

  render() {
    const {
      form: { getFieldDecorator },
      form,
    } = this.props;
    form.validateFields((error, fields) => {
      console.log(fields);
    });
    return (
      <div>
        <Form>
          <FormItem label={'商店实景图'}>
            {getFieldDecorator('pictures', {
              valuePropName: 'fileList',
              getValueFromEvent: this.handleOnChange,
            })(<PallWrop imgNumber={3} />)}
          </FormItem>
          <FormItem label={'商店实景图'}>
            {getFieldDecorator('ddd', {
              valuePropName: 'fileList',
              getValueFromEvent: this.handleOnChange,
            })(<PallWrop imgNumber={1} />)}
          </FormItem>
        </Form>
      </div>
    );
  }
}







// ================================================================================================







const mapStateToProps = state => ({
    auth: state.auth
})
const PostCommodity =  connect(mapStateToProps )(postCommodity)
const NewPost = Form.create({ name: 'create_new_post' })(newPost)

export default PostCommodity
