import React, { Component } from 'react'
import { theUrl } from 'selfConfig'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { Card, Collapse } from 'antd';

// import component
const Panel = Collapse.Panel;

class App extends Component {
  constructor(props) {
    super(props)
    this.getList = this.getList.bind(this)
    this.callback = this.callback.bind(this)

    this.state = {
        url: theUrl + '/commodityList'
    }
  }
  componentDidMount() {
      this.getList(this.state.url)
      console.log("App loaded")
  }   

  getList = (getAdminList) => {

      fetch(getAdminList, {
          method: 'get',
      })
      .then(res => { return res.json() })
      .then(res => {
          this.props.init(res)
          // Debugging
          console.log(res)
      })
      .catch(e=> {
          console.log("token expired")

      })
  }

  callback(key) {
    console.log(key)
  }

  render() {
    return(
      <div style={ { background: '#ECECEC' } }>
        <div>
          <h1>Hello react</h1>
          <Link to="/user_profile">To user profile  || </Link>
          <Link to="/root_dashboard">To root dashboard  || </Link>
          <Link to="/admin_dashboard">To admin dashboard || </Link>
          <Link to="/signup">Sign up</Link>
        </div>

        <div>
          {this.props.list.map((item, index)=>
            <Card
              key={item.id}
              hoverable
              style={ {width: 300,  margin: '30px'} }
              cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
            >
            <p>Commodity: {item.commodity_name}</p>
            <p>Price: {item.price}</p>
            <Collapse defaultActiveKey={['1']} onChange={this.callback}
            >
            <Panel header="See Details">
              <p>Seller: {item.username}</p>
              <p>date: { item.post_time }</p>
              <p>Description: {item.commodity_des}</p>
            </Panel>
            <Panel header="Comments">

            </Panel>
            </Collapse>
            </Card>
          )}
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  list: state.list
})

const mapDispatchToProps = dispatch => ({
  init: res => dispatch({
    type: 'INIT',
    res
  })
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
