const list = (state=[], action) => {
    switch (action.type) {

        // init user, admin , commodity
        case 'INIT':
            return action.res;

        case 'FETECH_DATA_BEGIN':
            return {
                loading: true
            };

        case 'FETECH_DATA_SUCCESS':
            return { 
                ...action.payload[0],
                loading: false,
            };

        // homepage
        case 'tFETECH_DATA_BEGIN':
            return {
                loading: true
            };

        case 'tFETECH_DATA_SUCCESS':
            return { 
                payload: action.payload,
                loading: false,
            };


        // Delete list item
        case 'DEL_ITEM':
            return state.filter(element => element.id !== action.id)

        default:
            return state

        // INIT homepage
        case 'HOME_INIT':
            return {
                loading: true
            }

        case 'HOME_INIT_SUCCESS':
            return {
                loading: false,
                payload: action.payload
            }
    }
}

export default list;
