import React from 'react'
import { connect } from 'react-redux'
import { theUrl } from 'selfConfig'

const mapStateToProps = state => ({
    auth: state.auth
})

const mapDispatchToProps = dispatch => ({
    login: (username, level) => {dispatch({
        type: 'LOGIN',
        username: username,
        level: level
    })}
})

const RootLogin = (props) => {

    let from = props.location
    let username = ''
    let password = ''

    var login = (e) => {
        e.preventDefault()

        const url=theUrl+"/adminLogin"
        fetch(url, {
            headers: {
                'Accept':'appliction/json',
                'Content-Type':'application/json'
            },
            method: 'POST',
            body: JSON.stringify({
                username: username.value,
                password: password.value
            })
        })
        .then(res=> { return res.json() })
        .then(res=> {

            // save token to redux
            props.login(res.username, res.level)

            // save token at browser
            localStorage.setItem('token', res.token)
            localStorage.setItem('level', res.level)

            props.history.push("/root_dashboard")

        })
        .catch(e=> {
            alert("login failed, check your username or password")
        })
    }


    return (
        <form onSubmit={login} >
            <h1>Root Login</h1>
            <label>
                <input 
                type="text" 
                placeholder="username" 
                ref={ input => username = input }
                />
            </label>
            <label>
                <input 
                type="password"
                placeholder="password"
                ref={ input => password = input }
                />
            </label>
            <input type="submit" value="login"/>
        </form>
    )
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(RootLogin)
