import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import {
    Layout, Menu, Breadcrumb, Icon,
} from 'antd';
// self
import { staticAdminUrl } from '../config'
import { LogoutButton, PreRoute } from '../public_component/index'

// route config
const admin = () => (
    <h1>admin</h1>
)

const user = () => (
    <h1>user</h1>
)

const routes = [
    {
        path: '/admin',
        component: admin
    },
    {
        path: '/user',
        component: user
    }
]


// page
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;


const Dashboard  = ({auth, match}) => {

    return(
        <div>
            <Layout>
                <Header>
                    <LogoutButton />
                    <img  src={staticAdminUrl+auth.username+".jpg"}  alt="avatar" />
                    <h1> Root DashBoard</h1>
                    <h2>{ auth.username }</h2>
                    <h2>{ auth.level }</h2>
                    <Link to="/">TO HOMEPAGE</Link>
                </Header>

                <Layout>
                    <Sider>
                    <ul style={{ listStyleType: "none", padding: 0 }}>
                        <li>
                            <Link to="/root_dashboard/admin">admin</Link>
                        </li>
                        <li>
                            <Link to="/root_dashboard/user">user</Link>
                        </li>
                    </ul>
                    </Sider>

                    <Content>
                        {routes.map((route, index) => (
                            <PreRoute 
                                key={index}
                                match={match} 
                                route={route} 
                            />
                        ))}
                    </Content>
                </Layout>
            </Layout>
        </div>
    )
}

const mapStateToProps = state => ({
    auth: state.auth
})

export default connect(mapStateToProps)(Dashboard);
