import {
    Layout, Menu, Avatar
} from 'antd';


// self
import { staticAdminUrl } from '../config'
import { LogoutButton, PreRoute } from '../public_component/index'
import List from './admin/list'

// route config
const routes = [
    {
        path: '/admin',
        component: List
    },
    {
        path: '/user',
        component: user
    }
]


// page
const { Header, Content, Sider } = Layout;
const Dashboard  = ({auth, match}) => {

    return(
        <div>
            <Layout>
                <Header>
                    {/* <LogoutButton />
                    <img  src={staticAdminUrl+auth.username+".jpg"}  alt="avatar" />
                    <h1> Root DashBoard</h1>
                    <h2>{ auth.username }</h2>
                    <h2>{ auth.level }</h2>
                    <Link to="/">TO HOMEPAGE</Link> */}

                    <Menu
                        theme="dark"
                        mode="horizontal"
                        style={{ lineHeight: '64px' }}
                    >
                    <Avatar src={staticAdminUrl+auth.username+".jpg"} />
                    <Menu.Item key="1">{auth.username}</Menu.Item>
                    <Menu.Item key="2">{auth.level}</Menu.Item>
                    <Menu.Item key="3"><Link to="/">HomePage</Link></Menu.Item>
                    <Menu.Item key="4"><LogoutButton /></Menu.Item>
                    </Menu>
                </Header>

                <Layout>
                    <Sider width={200} style={{ background: '#fff' }} >                    
                    {/* <ul style={{ listStyleType: "none", padding: 0 }}>
                        <li>
