const list = (state=[], action) => {
    switch (action.type) {
        case 'INIT_LIST':
            return action.res;

        case 'ADD_ITEM': 
            return [
                ...state,
                {
                    id: action.id,
                    username: action.username,
                    phone: action.phone
                }
            ]

        case 'DEL_ITEM':
            return []


        default:
            return state
    }
}

export default list;
