const auth = (state={}, action) => {
    switch (action.type) {
        case 'LOGOUT':
         return {
             username: '',
             logged: false,
         }

        case 'LOGIN': 
         return {
             username: action.username,
             logged: true,
         }
        default:
         return {
             username: '',
             logged: false,
         }
    }
}

export default auth;
