export default () => {
    let username = ''
    let password = ''

    var login = (e) => {
        e.preventDefault()

        const url="http://localhost:5000/login"
        fetch(url, {
            headers: {
                'Accept':'appliction/json',
                'Content-Type':'application/json'
            },
            method: 'POST',
            body: JSON.stringify({
                username: username.value,
                password: '123456'
            })
        })
        .then(res=> { return res.json() })
        .then(res=> {
            console.log(res)
        })
    }


    return (
        <form onSubmit={login} >
            <label>
                <input 
                type="text" 
                placeholder="username" 
                ref={ input => username = input }
                />
            </label>
            {/* <label>
