// Accept payload
// Accept category to filter category
// Accept styled card component to show data


import React , { Component }from 'react'
import { 
    Icon,
    Row, Col
  } from 'antd'






class PostCard extends Component {
    constructor(props) {
      super(props)
      this.state = {
        payload: [],
        category: ' ',
        loading: true
      }
    }

    componentDidMount() {
      let { category, payload } = this.props
      this.setState({
        payload: payload,
        category: category
      })
    }

    render() {
      if (this.state.loading) {
        let { component: StyleCard } = this.props
        let { payload, category} = this.state
        console.log(payload)
        // filter commodity,  In default, it returns all commodity
        let cate = category || -1
        let filterPayload

        if( cate < 0 ) {
          filterPayload = payload
        } else { 
          filterPayload = payload.filter(item=>{ return item.category === category })
        }

        return(
          <Row gutter={16 + 32} type="flex" justify="left">{
            filterPayload.map((item)=> {
              return <Col  xs={24} xl={6} >

                <StyleCard item={item} key={item.cid}/>

              </Col>})
          }
          </Row>
        )
      }

    }

}

export default PostCard;
