    actions: [<span>Reply to</span>],
    author: 'Han Solo',
    avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
    content: (
      <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
    ),
    datetime: (
      <Tooltip title={moment().subtract(1, 'days').format('YYYY-MM-DD HH:mm:ss')}>
        <span>{moment().subtract(1, 'days').fromNow()}</span>
      </Tooltip>
    ),
  },
  {
    actions: [<span>Reply to</span>],
    author: 'Han Solo',
    avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
    content: (
      <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
    ),
    datetime: (
      <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
        <span>{moment().subtract(2, 'days').fromNow()}</span>
      </Tooltip>
    ),
  },
];

// thunk data fetech action
const fetechData = () => {
  return dispatch => {  
      dispatch(fetechDataBegin())
      return fetch(theUrl + '/commodityList', {
          headers: tokenHeaders(localStorage.getItem("token")),
          method: 'post',
          body: JSON.stringify({
              username: localStorage.getItem("username")
          })
      })
      .then( res => res.json() )
      .then(json => {
          dispatch(fetechDataSuccess(json))
          return json;
      })
      .catch(err => {
          console.log(err)
      })
  }
}
const fetechDataBegin = () => ({
  type: 'tFETECH_DATA_BEGIN'
})
const fetechDataSuccess = (json) => ({
  type: 'tFETECH_DATA_SUCCESS',
  payload: json
})


const Editor = ({
  onChange, onSubmit, submitting, value,
}) => (
  <div>
    <Form.Item>
      <TextArea rows={4} onChange={onChange} value={value} />
    </Form.Item>
    <Form.Item>
      <Button
        htmlType="submit"
        // loading={submitting}
        onClick={onSubmit}
        type="primary"
      >
        Add Comment
      </Button>
    </Form.Item>
  </div>)


// import component
