import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { theUrl } from 'selfConfig'
import { LogoutButton } from '../public_component/index'

const mapStateToProps = state => ({
    auth: state.auth
})

// Get image from static source server
const staticUserUrl = theUrl+'/user'

const UserProfile = ({auth}) => {
    return (
        <div>
            <LogoutButton />
            <img  src={staticUserUrl+'/'+auth.username+".jpg"}  
                alt="avatar"
                onError="this.src='/user/default.jpg';this.onerror=null"
            />
            <h1>user profile</h1>
            <h2> {auth.username} </h2>
            <Link to="/">back to homepage</Link>
        </div>    
    )
}

export default connect(
    mapStateToProps
)(UserProfile)
