import React , { Component }from 'react'
import { theUrl } from 'selfConfig'
import { connect } from 'react-redux'
import { Table, Divider, Tag, Popconfirm } from 'antd'

// For the Table of admin list 
// const theColumn = [{
//     title: 'ID',
//     dataIndex: 'id',
//     key: 'id',
//   }, {
//     title: 'Username',
//     dataIndex: 'username',
//     key: 'username',
//   }, {
//     title: 'Phone',
//     dataIndex: 'phone',
//     key: 'phone',
// }, {
//     title: 'Action',
//     key: 'action',
//     render: (text, record) => (
//         <Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record.key)}>
//             <a href="javascript:;">Delete</a>
//         </Popconfirm>
//     ),
// }]

class List extends Component {
    constructor(props) {
        super(props)
        this.getList = this.getList.bind(this)
        this.delAdmin = this.delAdmin.bind(this)

        this.state = {
            url: theUrl + '/getAdminList',
            columns: [{
                title: 'ID',
                dataIndex: 'id',
                key: 'id',
              }, {
                title: 'Username',
                dataIndex: 'username',
                key: 'username',
              }, {
                title: 'Phone',
                dataIndex: 'phone',
                key: 'phone',
            }, {
                title: 'Action',
                key: 'action',
                render: (text, record) => (
                    <Popconfirm title="Sure to delete?" onConfirm={() => this.delAdmin(record.key)}>
                        <a href="javascript:;">Delete</a>
                    </Popconfirm>
                ),
            }],
        }
    }
    componentDidMount() {
        this.getList(this.props.dispatch, this.state.url)
    }   

    getList = (dispatch, getAdminList) => {

        fetch(getAdminList)
        .then(res => { return res.json() })
        .then(res => {
            this.props.init(res)
            // Debugging
            // console.log(res)
        })
    }

    delAdmin = (key) => {
        this.props.delAdmin(key)
    }

    render() {
        return(
            <div>

                <Table 
                    dataSource={this.props.list}
                    columns={this.state.columns}
                />
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => ({
    deleteAdmin : id => dispatch({
        type: 'DEL_ITEM',
        id
    }),
    init: res => dispatch({
        type: 'INIT',
        res
    })
})

const mapStateToProps = state => ({
    list: state.list
})
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(List);
