import { Route, Redirect } from 'react-router-dom'
import React from 'react'
import { connect } from 'react-redux'

const mapStateToProps = state => ({
    auth: state.auth
})

const ProtectRoute = ({
    component: Component,
    // auth,
    ...rest,
}) => (
    <Route {...rest} render={(props, auth)=>(
        true
        ?(<Component {...props} />)
        :(<Redirect to='/login' />)
    )} />
)


export default connect(
    mapStateToProps
)(ProtectRoute)
