import { Route, Redirect } from 'react-router-dom'
import React from 'react'
import { connect } from 'react-redux'

const mapStateToProps = state => ({
    auth: state.auth
})

const protectRoute = ({
    component: Component,
    auth,
    ...rest,
}) => {

    <Route {...rest} render={(props)=>(
        auth.logged
        ?<Component {...props}/>
        :<Redirect to='/login' />
    )} />
}

export default connect(
    mapStateToProps
)(protectRoute)
