// Accept payload
// Accept category to filter category
// Accept styled card component to show data


import React , { Component }from 'react'
import { 
    Icon,
    Row, Col
  } from 'antd'






class PostCard extends Component {
    constructor(props) {
      super(props)
      this.state = {
        payload: [],
        category: '',
        loading: true
      }

      this.onCardListChange = this.onCardListChange.bind(this)
    }

    componentDidMount() {
      this.setState({
        payload: this.props.payload,
        category: this.props.category,
        loding: false
      })
    }

    onCardListChange(cid) {
      this.setState({
        payload: this.state.payload.filter(item=>{ item.cid !== cid})
      })
    }

    render() {

      if (this.state.loding) {
        return <h1>Loding</h1>
      } 
      else {
        let { component: StyleCard } = this.props
        let { category, payload } = this.state

        // filter commodity,  In default, it returns all commodity
        let filterPayload
        if( this.state.category < 0 ) {
          filterPayload = payload
        } else { 
          filterPayload = payload.filter(item=>{ return item.category === category })
        }

        return(
          <Row gutter={16 + 32} type="flex" justify="left">{
            filterPayload.map((item)=> {
              return <Col  xs={24} xl={6} >

                <StyleCard item={item} key={item.cid} onChange={this.onCardListChange}/>

              </Col>})
          }
          </Row>
        )
      }
    }

}

export default PostCard;
