import { theUrl, tokenHeaders } from 'selfConfig'
// import CardList from './user/components/cardList'
import ShowCard from './user/components/showCard'
// import { LogoutButton } from './public_component/index'


import React, { Component } from 'react'
import axios from 'axios'
// import { connect } from 'react-redux'
// import { Link, Route } from 'react-router-dom'
// // import moment from 'moment'
// import { 
//   Input, 
//   Layout, Menu, Icon
// } from 'antd'
// const TextArea = Input.TextArea
// const { Header, Content, Footer, Sider } = Layout

import Countdown from 'react-countdown-now'


// const item = {
//   cid: 37,
//   pic_num: 1,
//   price: '200',
//   post_time: '2019-06-06 07:58:00',
//   commmodity_name: 'my cup',
//   commodity_desc: 'This cup was uesd by Mea, It\'s very precious.'
// }

class Auction extends Component {
  constructor(props) {
    super(props)
    this.state = {
      price : ''
    }
  }

  componentDidMount() {
    this.setState({
      price: this.props.init
    })
  }

  render() {
    return(
      <div>
        <h1>PRICE: </h1>
        <h1>{this.state.price}</h1>
      </div>
    )
  }
}

class App extends Component {
  constructor() {
    super()
    this.state= {
      item: {}
    }
  }

  componentDidMount() {
    axios.get(theUrl+'/commodityList')
    .then(res=>{
      this.setState({
        item: res.data[0]
      })
      console.log(res.data[0])
    })
  }

  render() {
    let { item }  = this.state
    let theDate = new Date(item.post_time)
    let start = theDate.getTime()



    return(
      <div>
        <ShowCard item={item}/>
        <Auction init={item.price}/>
        <Countdown date={start + 1000*3 } >
          <h1>竞拍结束</h1>
        </Countdown>
      </div>
    )
  }
}

export default App
