import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Form, Input, Button } from 'antd'

import { theUrl , tokenHeaders } from 'selfConfig'

class  newPost extends Component {
    constructor(props) {
        super(props)
        const { auth } = this.props
        this.state = {
            loading: true,
            username: auth.username,
            payload: []
        }
    }

    async componentDidMount() {
        const url = theUrl + '/userProfile'

        const payload = await fetch(url, {
            headers: tokenHeaders(localStorage.getItem("token")),
            method: 'POST',
            body: JSON.stringify({
            username: this.props.auth.username
            })
        }).then( res => res.json() )

        this.setState({
            loading: false,
            payload: payload[0]
        })
    }

    render() {
        const {payload} = this.state
        return(
            <Form>
                <Form.Item label="username">{payload.username}</Form.Item>
                <Form.Item label="phone number">{payload.phone}</Form.Item>
                <Form.Item labael="Wechat">
                    <img src={`${theUrl}/user/${payload.username}_wx.jpg`} />
                </Form.Item>
                <Form.Item>
                    <Input placeholder="commodity"/>
                </Form.Item>
                <Form.Item>
                    <Input placeholder="price"/>
                </Form.Item>

                <Form.Item>
                    <Input placeholder="description" />
                </Form.Item>
            </Form>
        )
    }
}

const LivingPostList = () => {
    return (
        <h1>Living Commodity List</h1>
    )
}



const PostCommodity = () => {
    return(
        <div>
            <NewPost />
            <LivingPostList />
        </div>
    )
}
const mapStateToProps = state => ({
    auth: state.auth
})
const NewPost = connect(mapStateToProps )(newPost)

export default PostCommodity
