import React , { Component }from 'react'
import { theUrl, tokenHeaders } from 'selfConfig'
import { 
    Card, Collapse, Comment, Tooltip, List, Button, Avatar, Form, Input, 
    Layout, Menu, Breadcrumb, Icon,
    Carousel,
    Modal
  } from 'antd'
import { runInThisContext } from 'vm';


class PostCard extends Component {
    constructor(props) {
      super(props)
      this.state = {
        visible: false
      }

      this.showModal = this.showModal.bind(this)
      this.handleOk = this.handleOk.bind(this)
      this.handleCancel = this.handleCancel.bind(this)
    }

    showModal = () => {
      this.setState({
        visible: true,
      });
    }

    handleOk = (e) => {
      console.log(e);
      this.setState({
        visible: false,
      });
    }

    handleCancel = (e) => {
      console.log(e);
      this.setState({
        visible: false,
      });
    }

    render() {
      const { payload, username } = this.props
      return(
        payload.reverse().map((item, index)=> {
            return <Card
            key={item.cid}
            hoverable
            style={{ width: 640, marginTop: 50 }}
            cover={<img alt="example" src={theUrl+`/commodity/${item.cid}_0.jpg`} />}
            >
              {/* show the pictures of commodity */}
              <Button
                onClick={this.showModal}
              >more picture
              </Button>
              <Modal
                title="Basic Modal"
                visible={this.state.visible}
                onOk={this.handleOk}
                onCancel={this.handleCancel}
              >
                <p>Some contents...</p>
                <p>Some contents...</p>
                <p>Some contents...</p>
              </Modal>

              {/* information */}
              <h5>post time: {item.post_time}</h5>
              <h1>{item.commodity_name}</h1>
              <h5 style={{ color: '#F22F08'}}>Price: {item.price}</h5>
              <h5>{item.post}</h5>
              <h5 style={{ color: "#0ABDA0" }}>Seller: {item.username}</h5>
              <h5>Categorys: {item.category}</h5>
              <h2 style={{ color: 'gray'}}>{item.commodity_des}</h2>
              <h5>Phone: {item.phone}</h5>
              <img src={`${theUrl}/user/${username}_wx.jpg`} />
            </Card>})
      )
    }

}

export default PostCard;
