import React, { Component } from  'react'
import { connect } from 'react-redux'
import { theUrl, tokenHeaders } from 'selfConfig'




class Profile extends Component {
    constructor(props) {
        super(props)
        this.initPage = this.initPage.bind(this)
        this.state = {
            url : theUrl + '/userProfile',
            username: this.props.auth.username
        }
    }

    componentDidMount() {
        this.initPage(this.state.url)
    }

    initPage(url) {
        fetch(url, {
            headers: tokenHeaders(localStorage.getItem("token")),
            method: 'post',
            body: JSON.stringify({
                username: this.state.username
            })
        })
        .then(res=>{ return res.json})
        .then(res => {
            this.props.init(res)
        })
    }

    render() {
        const { auth } = this.props
        return(
            <div>
            <h1>This is profile page, change your information here.</h1>
            <h1>username: {this.state.username}</h1>
            <h1>url {this.state.url}</h1>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    auth: state.auth
})
const mapDispatchToProps = dispatch => ({
    init: res => dispatch({
        type: 'INIT',
        res
    })
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Profile)
