const list = (state=[], action) => {
    switch (action.type) {

        // init user, admin , commodity
        case 'INIT':
            return action.res;

        // Add user                
        case 'ADD_ITEM': 
            return [
                ...state,
                {
                    id: action.id,
                    username: action.username,
                    phone: action.phone
                }
            ]

        // Delete user 
        case 'DEL_ITEM':
            return state.filter(element => element.id !== action.id)


        // Delete commodity
        case 'DEL_COMMODITY':
            return state.filter(element => element.id !== action.id )


        default:
            return state
    }
}

export default list;
