import React, { Component } from  'react'
import { connect } from 'react-redux'
import { theUrl, tokenHeaders } from 'selfConfig'
import { Form, Input } from 'antd'




class Profile extends Component {
    constructor(props) {
        super(props)
        this.initPage = this.initPage.bind(this)
        this.state = {
            url : theUrl + '/userProfile',
            username: this.props.auth.username,

        }
    }

    componentDidMount() {
        this.initPage(this.state.url)
    }

    initPage(url) {
        fetch(url, {
            headers: tokenHeaders(localStorage.getItem("token")),
            method: 'post',
            body: JSON.stringify({
                username: this.state.username
            })
        })
        .then(res=>{ return res.json() })
        .then(res => {
            this.props.init(res)
            this.setState({
                neckname: res[0].neckname
            })
        })
    }

    render() {
        const { getFieldDecorator } = this.props.form
        const { list, auth } = this.props
        return(
            <Form>
                <Form.Item label="Username: ">
                    <label>{auth.username}</label>
                </Form.Item>

                <Form.Item label="Neck name: ">
                    {getFieldDecorator('neckname', {})(
                        // <Input placeholder={this.state.id}  />
                        <label>call me { list[0].username }</label>
                    )}
                </Form.Item>
            </Form>
        )
    }
}

const mapStateToProps = state => ({
    auth: state.auth,
    list: state.list
})
const mapDispatchToProps = dispatch => ({
    init: res => dispatch({
        type: 'INIT',
        res
    })
})

const theProfile = Form.create({ name: 'normal_login' })(Profile);

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(theProfile)
