import React , { Component }from 'react'
import { theUrl, tokenHeaders } from 'selfConfig'
import { 
    Card, Collapse, Comment, Tooltip, List, Button, Avatar, Form, Input, 
    Layout, Menu, Breadcrumb, Icon,
    Carousel,
    Modal
  } from 'antd'
import Zmage from 'react-zmage'

class PostCard extends Component {
    constructor(props) {
      super(props)
      this.state = {
        visible: false
      }

      this.showModal = this.showModal.bind(this)
      this.handleOk = this.handleOk.bind(this)
      this.handleCancel = this.handleCancel.bind(this)
      this.getPicList = this.getPicList.bind(this)
    }

    showModal = () => {
      this.setState({
        visible: true,
      });
    }

    handleOk = (e) => {
      console.log(e);
      this.setState({
        visible: false,
      });
    }

    handleCancel = (e) => {
      console.log(e);
      this.setState({
        visible: false,
      });
    }

    getPicList = async (pic_num, cid) => {
      let list = []
      for(var i = 0; i < pic_num; i++) {
        list[i] = await { src: theUrl+`/commodity/${cid}_${i}.jpg`}
      }
      return list

      // // console.log(list)
      // return list

      // return [
      //   {src: theUrl+`/commodity/19_0.jpg`},
      //   {src: theUrl+`/commodity/19_1.jpg`},
      // ]
    }

    render() {
      let { payload, username } = this.props

      return(
        payload.map((item)=> {
            let picList = this.getPicList(item.pic_num, item.cid)
            return <Card
            key={item.cid}
            hoverable
            style={{ width: 640, marginTop: 50, padding: "20px", }}
            cover={<Zmage 
                      style={{heigth: "10rem", weight: "100%", overflow: "hidden"}}
                      src={theUrl+`/commodity/${item.cid}_0_thumb.jpg`}
                      set={[
                        {src:theUrl+`/commodity/${item.cid}_0.jpg`},
                        {src:theUrl+`/commodity/${item.cid}_1.jpg`}
                      ]}
                    />
                  }
            >

            {/* show the pictures of commodity */}
            {/* <div style={{ display: "flex", alignItems: "center"   }}>
              {<Zmage 
                  style={{heigth: "10rem", weight: "100%", overflow: "hidden"}}
                  src={theUrl+`/commodity/${item.cid}_0_thumb.jpg`}
                  set={this.getPicList(item.pic_num, item.cid)}
                />
              }

            </div> */}


              {/* information */}
              <div>
                <h5>post time: {item.post_time}</h5>
                <h1>{item.commodity_name}</h1>
                <h5 style={{ color: '#F22F08'}}>Price: {item.price}</h5>
                <h5>{item.post}</h5>
                <h5 style={{ color: "#0ABDA0" }}>Seller: {item.username}</h5>
                <h5>Categorys: {item.category}</h5>
                <h2 style={{ color: 'gray'}}>{item.commodity_des}</h2>
                <h5>Phone: {item.phone}</h5>
              </div>

              {/* contact */}
              <div style={{display: "flex",
                          justifyContent: "center",
                          alignItems: "center"  }}
              >
               <img 
                src={`${theUrl}/user/${username}_wx.jpg`} 
                style={{ align: 'center' }}
                />
              </div>
            </Card>})
      )
    }

}

export default PostCard;
