import React from 'react'
import { Upload, Icon, Modal, Button, message } from 'antd'
import { theUrl } from 'selfConfig'

class Picwall extends React.Component {
  constructor(props) {
    super(props)
    this.handleEvent = this.handleEvent.bind(this)
  }


  handleEvent({file, fileList})  {
    console.log("handleEvent: ", file, fileList);
    // this.props.form.setFieldsValue({uploadComTest: fileList}); ???

    //return fileList; ???
  }


  render() {
    const {getFieldDecorator,} = this.props.form;
    // console.log("this.props.from: ", this.props.form);
    const props = {
      action: "/upload.do",
      listType: "picture",
      onChange: this.handleUpload,
    };
    return (
        <div >
          {getFieldDecorator('uploadComTest', {
            valuePropName: 'fileList',
            initialValue: [
              {
                uid: -1,
                name: 'xxx.png',
                status: 'done',
                url: 'http://www.baidu.com/xxx.png',
              }, {
                uid: -2,
                name: 'yyy.png',
                status: 'done',
                url: 'http://www.baidu.com/yyy.png',
              }],

             getValueFromEvent: this.handleEvent,

          })(
            <Upload name="logo" {...props}>
              <Button type="ghost">
                <Icon type="upload" /> Click to upload
              </Button>
            </Upload>
          )}
        </div>
    );
  }
}

export default Picwall
