import React, { Component } from 'react'
import { connect } from 'react-redux'
import { theUrl } from 'selfConfig'


import EditCard from './editCard'
import CardList from './cardList'
import ShowCard from './showCard'

class UserPostList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
            payload: [],
            username: this.props.username 
        }
    }

    async componentDidMount() {
        const url = theUrl+'/commodityList';
        // const payload = await fetch(url).then( res => res.json() )
        const payload = await fetch(url, {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            method: 'post',
            body: JSON.stringify({
                status: 1,
                username: this.props.auth.username
            })
        })
        .then( res => res.json() )

        this.setState({
          loading: false,
          payload: payload,
        })

    }

    render() {
        const { payload, loding } = this.state

        if (loding) {
            return (<h1>Loding</h1>)
        } else {
            return(
                <div>
                    <h1>Living Post List</h1>
                    <CardList payload={payload} category={-1} component={ShowCard} />
                </div>
            )
        }
    }
}

const mapStateToProps = state => ({
    auth: state.auth
})
export default connect(mapStateToProps)(UserPostList)
