import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { staticAdminUrl } from '../config'
import { LogoutButton, PreRoute } from '../public_component/index'

const admin = () => (
    <h1>admin</h1>
)

const user = () => (
    <h1>user</h1>
)

const routes = [
    {
        path: '/admin',
        component: admin
    },
    {
        path: '/user',
        component: user
    }
]

// const PreRoute = ({ match, route}) => (
//     <Route 
//         path={`${match.url}`+route.path}
//         component={route.component}
//     />
// )


const Dashboard  = ({auth, match}) => {

    return(
        <div>
            <div className="navbar" style={{ backgroundColor: "gray"}}>
                <LogoutButton />
                <img  src={staticAdminUrl+auth.username+".jpg"}  alt="avatar" />
                <h1> Root DashBoard</h1>
                <h2>{ auth.username }</h2>
                <h2>{ auth.level }</h2>
                <Link to="/">TO HOMEPAGE</Link>
            </div>{/* navbar */}

            <div className="sidebar">
            <ul style={{ listStyleType: "none", padding: 0 }}>
                <li>
                    <Link to="/root_dashboard/admin">admin</Link>
                </li>
                <li>
                    <Link to="/root_dashboard/user">user</Link>
                </li>
            </ul>
            </div>{/* sidebar */}

            <div className="content">
                {routes.map((route, index) => (
                    <PreRoute 
                        key={index}
                        match={match} 
                        route={route} 
                    />
                ))}
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    auth: state.auth
})

export default connect(mapStateToProps)(Dashboard);
