import React, { Component } from 'react'
import { theUrl } from 'selfConfig'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import moment from 'moment'
import { 
  Card, Collapse, Comment, Tooltip, List, Button, Avatar, Form, Input, 
  Layout, Menu, Breadcrumb, Icon
} from 'antd'
const TextArea = Input.TextArea
const {
  Header, Content, Footer, Sider
} = Layout
const { SubMenu } = Menu

// monitor data
const data = [
  {
    actions: [<span>Reply to</span>],
    author: 'Han Solo',
    avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
    content: (
      <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
    ),
    datetime: (
      <Tooltip title={moment().subtract(1, 'days').format('YYYY-MM-DD HH:mm:ss')}>
        <span>{moment().subtract(1, 'days').fromNow()}</span>
      </Tooltip>
    ),
  },
  {
    actions: [<span>Reply to</span>],
    author: 'Han Solo',
    avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
    content: (
      <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
    ),
    datetime: (
      <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
        <span>{moment().subtract(2, 'days').fromNow()}</span>
      </Tooltip>
    ),
  },
];



const Editor = ({
  onChange, onSubmit, submitting, value,
}) => (
  <div>
    <Form.Item>
      <TextArea rows={4} onChange={onChange} value={value} />
    </Form.Item>
    <Form.Item>
      <Button
        htmlType="submit"
        // loading={submitting}
        onClick={onSubmit}
        type="primary"
      >
        Add Comment
      </Button>
    </Form.Item>
  </div>)


// import component
const Panel = Collapse.Panel;

class App extends Component {
  constructor(props) {
    super(props)
    this.getList = this.getList.bind(this)
    // this.callback = this.callback.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)

    this.state = {
        url: theUrl + '/commodityList'
    }
  }
  componentDidMount() {
      this.getList(this.state.url)
  }   

  getList = (getAdminList) => {

      fetch(getAdminList, {
          method: 'get',
      })
      .then(res => { return res.json() })
      .then(res => {
        // Debugging
        console.log(res)
        this.props.init(res)
      })
      .catch(e=> {
          console.log("token expired")

      })
  }

  // callback(key) {
  //   console.log(key)
  // }

  handleSubmit = () => {
    if (!this.state.value) {
      return;
    }
  }

  render() {
    const value = ''
    const submitting = 'true'

    return(
    <Layout>
    <Header className="header">
      <div className="logo" />
      <Menu
        theme="dark"
        mode="horizontal"
        defaultSelectedKeys={['3']}
        style={{ lineHeight: '64px' }}
      >
        <Menu.Item key="1"><Link to="/user_profile">Profile</Link></Menu.Item>
        <Menu.Item key="2"><Link to="/signup">Signup</Link></Menu.Item>
        <Menu.Item key="3"><Link to="/">Home</Link></Menu.Item>

      </Menu>
    </Header>
    <Content style={{ padding: '0 50px' }}>
      <Layout style={{ padding: '24px 0', background: '#fff' }}>
        <Sider width={200} style={{ background: '#fff' }}>
          <Menu
            mode="inline"
            defaultSelectedKeys={['1']}
            defaultOpenKeys={['sub1']}
            style={{ height: '100%' }}
          >
              <Menu.Item key="1">书籍</Menu.Item>
              <Menu.Item key="2">电子产品</Menu.Item>
              <Menu.Item key="3">外快</Menu.Item>
              <Menu.Item key="4">others</Menu.Item>
          </Menu>
        </Sider>
        <Content style={{ padding: '0 24px', minHeight: 280, background: '#ECECEC', }}>
        {this.props.list.map((item, index)=>
              <Card
                key={item.id}
                hoverable
                style={ {width: 400,  margin: '30px'} }
                cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
              >
              <p>Commodity: {item.commodity_name}</p>
              <p>Price: {item.price}</p>
              <Collapse onChange={this.callback}
              >
              <Panel header="See Details">
                <p>Seller: {item.username}</p>
                <p>date: { item.post_time }</p>
                <p>Description: {item.commodity_des}</p>
              </Panel>
              <Panel header="Comments">
                <Comment 
                  avatar={(
                    <Avatar
                    src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                    alt="Han Solo"
                    />
                    )}

                  content={(
                    <Editor
                    onChange={()=>{ console.log("clicked")}}
                    onSubmit={ this.handleSubmit }
                    submitting={ submitting }
                    value={value}
                    />
                    )}
                />
                <List
                  className="comment-list"
                  header={`${data.length} replies`}
                  itemLayout="horizontal"
                  dataSource={data}
                  renderItem={props=> 
                    <Comment {...props}/>}
                />
              </Panel>
              </Collapse>
              </Card>
            )}
        </Content>
      </Layout>
    </Content>
    <Footer style={{ textAlign: 'center' }}>
      Nangua ©2018 Created by Danker
    </Footer>
  </Layout>)
  }
}

const mapStateToProps = state => ({
  list: state.list
})

const mapDispatchToProps = dispatch => ({
  init: res => dispatch({
    type: 'INIT',
    res
  })
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
