import React from 'react'
import { theUrl, tokenHeaders } from 'selfConfig'
import { 
    Card, Collapse, Comment, Tooltip, List, Button, Avatar, Form, Input, 
    Layout, Menu, Breadcrumb, Icon,
    Carousel,
    Modal
  } from 'antd'
import { runInThisContext } from 'vm';


const PostCard = ({ payload, username }) => {

    var state = {
      visible: false
    }

    const showModal = () => {
      state.visble = true
    }

    const handleOk = (e) => {
      state.visable = false
    }

    const handleCancel = (e) => {
      // console.log(e);
      // this.setState({
      //   visible: false,
      // });
    }

    return(
        payload.reverse().map((item, index)=> {
            return <Card
            key={item.cid}
            hoverable
            style={{ width: 640, marginTop: 50 }}
            cover={<img alt="example" src={theUrl+`/commodity/${item.cid}_0.jpg`} />}
            onClick={showModal}
            >
              {/* show the pictures of commodity */}
              <Modal
                title="Basic Modal"
                visible={state.visible}
                onOk={handleOk}
                onCancel={handleCancel}
              >
                <p>Some contents...</p>
                <p>Some contents...</p>
                <p>Some contents...</p>
              </Modal>

              {/* information */}
              <h5>post time: {item.post_time}</h5>
              <h1>{item.commodity_name}</h1>
              <h5 style={{ color: '#F22F08'}}>Price: {item.price}</h5>
              <h5>{item.post}</h5>
              <h5 style={{ color: "#0ABDA0" }}>Seller: {item.username}</h5>
              <h5>Categorys: {item.category}</h5>
              <h2 style={{ color: 'gray'}}>{item.commodity_des}</h2>
              <h5>Phone: {item.phone}</h5>
              <img src={`${theUrl}/user/${username}_wx.jpg`} />
            </Card>})
    )
}

export default PostCard;
